import React from 'react';
import ReactDOM from 'react-dom';
import { 
  View,
  Panel,
  PanelHeader,
  PanelHeaderBack,
  Root,
  CellButton,
  Group,
  FormLayout,
  FormLayoutGroup,
  Input,
  Select,
  Textarea,
  Radio,
  Button,
  Epic,
  Tabbar,
  TabbarItem
} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';
import logo from "./images/logoFull.png";
import { func } from 'prop-types';

class SelectW extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
        1: "1 кг",
        5: "5 кг",
        10: "10 кг",
        15: "15 кг",
    }
  }

  render() {
    return(
      <Select top="Вес посылки">
        <option name="weight" value="1">До {this.state[1]}</option>
        <option name="weight" value="5">До {this.state[5]}</option>
        <option name="weight" value="10">До {this.state[10]}</option>
        <option name="weight" value="15">До {this.state[15]}</option>
      </Select>
    )
  }
}

class SelectA extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      50: "50 кг",
      100: "100 кг",
      150: "150 кг",
      200: "200 кг",
    }
  }

  render() {
    return (
      <Select top="Вес посылки">
        <option name="weight" value="1">До {this.state[50]}</option>
        <option name="weight" value="5">До {this.state[100]}</option>
        <option name="weight" value="10">До {this.state[150]}</option>
        <option name="weight" value="15">До {this.state[200]}</option>
      </Select>
    )
  }
}

function SelectWA(props) {
  const isWalk = props.isWalk;

  if (isWalk) {
    return <SelectW />;
  } else {
    return <SelectA />;
  }

  return(
    <FormLayoutGroup top="Тип доставки">
      <Radio
        name="radio"
        value="w"
        id="radio-w"
        checked
      >Пешком</Radio>
      <Radio
        name="radio"
        value="a"
        id="radio-a"
      >Авто</Radio>
    </FormLayoutGroup>
  );
}

class Form extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      addressFrom: '',
      addressTo: '',
