import React from 'react';
import ReactDOM from 'react-dom';
import {
    View,
    Panel,
    PanelHeader,
    PanelHeaderBack,
    Card,
    CardGrid,
    Text
} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

class Courier extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            activeView: ''
        }

        this.order = {
            addressFrom: 'ул. Д. Донского, д.1',
            addressTo: 'ул. Марата, д.4',
            timeFrom: '15:30',
            timeTo: '17:00',
            dayFrom: '',
            dayTo: '',
            telTo: '',
            telFrom: '',
            commentsFrom: '',
            commentsTo: '',
            additionFrom: '',
            additionTo: '',
            price: '100'
        }
    }

    render() {
        return(
            <div>
                <CardGrid style={{marginTop: 5}}>
                    <Text>Заказ №_____</Text>
                    <Card size="l" mode="shadow">
                        <div style={{
                            minHeight: 96,
                            display: "flex",
                            padding: 10
                        }}>
                            <div style={{
                                borderRight: "1px solid #ccc",
                                paddingRight: 10,
                                color: "#ff5c00"
                            }}>
                                {this.order.price} Руб.
                            </div>
                            <div>
                                <Text>Приехать до {this.order.timeFrom}</Text>
                            </div>
                        </div>
                    </Card>
                </CardGrid>
            </div>
        );
    }
}

export default Courier;
