import React from 'react';
import ReactDOM from 'react-dom';
import orders from '../../orders/orders.json';
import {
    View,
    Panel,
    PanelHeader,
    PanelHeaderBack,
    Card,
    CardGrid,
    Text,
    Div,
    ModalRoot,
    ModalPage,
    ModalPageHeader,
    PanelHeaderButton,
    IS_PLATFORM_ANDROID,
    IS_PLATFORM_IOS,
    FormLayout,
    Button
} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

class CardItem extends React.Component {
    render() {
        return(
            <CardGrid style={{marginTop: 5}}>
                <Text>Заказ №{props.array[0].id}</Text>
                <Card size="l" mode="shadow" onClick={()=> this.setState({activeModal: "order"})}>
                    <div style={{
                        minHeight: 96,
                        display: "flex",
                        padding: 10
                    }}>
                        <div style={{
                            borderRight: "1px solid #ccc",
                            paddingRight: 10,
                            color: "#ff5c00",
                            fontSize: 14
                        }}>
                            100 Руб.
                        </div>
                        <div style={{
                            padding: "0 0 0 10px"
                        }}>
                            <Text style={{color: "#c6c6c6"}}>
                                Приехать до <span style={{fontWeight: "bold", color: "#ff5c00"}}>
                                    15:30
                                </span>
                            </Text>
                            <Text style={{
                                color: "#c6c6c6"
                            }}>От: <span style={{
                                color: "#ff5c00"
                            }}>ddd</span></Text>
                            <Text style={{
                                color: "#c6c6c6"
                            }}>До: <span style={{
                                color: "#ff5c00"
                            }}>eee</span></Text>
                            <Text style={{
                                color: "#ff5c00",
                                marginTop: 15,
                                // borderTop: "1px solid #c6c6c6"
                            }}>
                                ttt до qqq кг.
                            </Text>
                        </div>
                    </div>
                </Card>
            </CardGrid>
        );
    }
}

