    constructor(props) {
        super(props);

        this.state = {
            activeModal: null
        }

        this.order = {
            orderNum: "846375",
            weight: "1",
            addressFrom: 'ул. Д. Донского, д.1',
            addressTo: 'ул. Марата, д.4',
            timeFrom: '15:30',
            timeTo: '17:00',
            dayFrom: '',
            dayTo: '',
            telTo: '',
            telFrom: '',
            commentsFrom: '',
            commentsTo: '',
            additionFrom: '',
            additionTo: '',
            carry: 'Документы',
            price: '100'
        }

        this.modalBack = () => {
            this.setActiveModal(null);
        }
    }

    setActiveModal(activeModal) {
        this.setState({activeModal});
    }

    render() {
        let orderArr = [];
        for (let i = 0; i < orders.length; i++) {

        }

        console.log(orders);

        const modal = (
            <ModalRoot
                activeModal={this.state.activeModal}
                onClose={this.modalBack}
            >
                <ModalPage
                    id="order"
                    onClose={this.modalBack}
                    header={
                        <ModalPageHeader
                            right={<PanelHeaderButton onClick={this.modalBack}>{'Готово'}</PanelHeaderButton>}
                        >Заказ №{this.order.orderNum}</ModalPageHeader>
                    }
                >

                </ModalPage>
            </ModalRoot>
        );
        return(
            <View modal={modal}>
                {orders.map(() => (
                    <CardItem array={orders} />
                ))}
            </View>
        );
    }
}

