import React from 'react';
import ReactDOM from 'react-dom';
import { 
  View,
  Panel,
  PanelHeader,
  PanelHeaderBack,
  Root,
  CellButton,
  Group,
  FormLayout,
  FormLayoutGroup,
  Input,
  Select,
  Textarea,
  Radio,
  Button,
  Epic,
  Tabbar,
  TabbarItem
} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';
import logo from "./images/logoFull.png";
import { func } from 'prop-types';

class SelectW extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
        1: "1 кг",
        5: "5 кг",
        10: "10 кг",
        15: "15 кг",
    }
  }

  render() {
    return(
      <Select top="Вес посылки">
        <option name="weight" value="1">До {this.state[1]}</option>
        <option name="weight" value="5">До {this.state[5]}</option>
        <option name="weight" value="10">До {this.state[10]}</option>
        <option name="weight" value="15">До {this.state[15]}</option>
      </Select>
    )
  }
}

class SelectA extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      50: "50 кг",
      100: "100 кг",
      150: "150 кг",
      200: "200 кг",
    }
  }

  render() {
    return (
      <Select top="Вес посылки">
        <option name="weight" value="1">До {this.state[50]}</option>
        <option name="weight" value="5">До {this.state[100]}</option>
        <option name="weight" value="10">До {this.state[150]}</option>
        <option name="weight" value="15">До {this.state[200]}</option>
      </Select>
    )
  }
}

function Radios(props) {

  const isWalk = true;
  let out = '';

  if(isWalk) {
    out = <Select top="Вес посылки">;
    out += <option name="weight" value="1">До 1 кг.</option>;
    out += <option name="weight" value="5">До 5 кг.</option>;
    out += <option name="weight" value="10">До 10 кг.</option>;
    out += <option name="weight" value="15">До 15 кг.</option>;
    out += </Select>;
  } else {
    out = <Select top="Вес посылки">;
    out += <option name="weight" value="50">До 50 кг.</option>;
    out += <option name="weight" value="100">До 100 кг.</option>;
    out += <option name="weight" value="150">До 150 кг.</option>;
    out += <option name="weight" value="200">До 200 кг.</option>;
    out += </Select>;
  }

  return(
    <FormLayoutGroup top="Тип доставки">
      <Radio
        name="radio"
        value="w"
        id="radio-w"
        checked
        onClick={isWalk=true}
      >Пешком</Radio>
      <Radio
        name="radio"
        value="a"
        id="radio-a"
        onClick={isWalk=false}
      >Авто</Radio>
      {out}
    </FormLayoutGroup>
  );
}

class Form extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      addressFrom: '',
      addressTo: '',
      timeFrom: '',
      timeTo: '',
      dayFrom: '',
      dayTo: '',
      telTo: '',
      telFrom: '',
      commentsFrom: '',
      commentsTo: '',
      additionFrom: '',
      additionTo: '',
      price: '100'
    }

    this.onChange = this.onChange.bind(this);
  }

  onChange(e) {
    const { name, value } = e.currentTarget;
    this.setState({ [name]: value });
  }

  render() {
    const { 
      addressFrom,
      addressTo,
      timeFrom,
      timeTo,
      dayFrom,
      dayTo,
      telTo,
      telFrom,
      commentsFrom,
      commentsTo,
      additionFrom,
      additionTo 
    } = this.state;

    return (
          <FormLayout>

            <Radios />

            <FormLayout>
              <hr style={{
                height: 2,
                background: "#ff5c00",
                border: "none"
              }} />
              <Group style={{
                paddingLeft: 16,
                color: "#ff5c00",
                fontSize: 32,
                }}>Откуда</Group>
              <Input
                top="Адрес"
                type="text"
                name="addressFrom"
                value={addressFrom}
                onChange={this.onChange}
              />
              <FormLayout>
                <Select 
                  top="Когда и до скольки приехать"
                  name="dayFrom"
                  value={dayFrom}
                  onChange={this.onChange}
                >
                  <option>Сегодня</option>
                  <option>Завтра</option>
                </Select>
                <Input
                  type="time"
                  name="timeFrom"
                  value={timeFrom}
                  onChange={this.onChange}
                />
              </FormLayout>
              <Input
                top="Телефон"
                type="tel"
                name="telFrom"
                value={telFrom}
                onChange={this.onChange}
              />
              <Input
                top="Коментарий: кв., офис и т.д."
                type="text"
                name="commentsFrom"
                value={commentsFrom}
                onChange={this.onChange}
              />
              <Textarea
                top="Дополнительно"
                name="additionFrom"
                value={additionFrom}
                onChange={this.onChange}
              />
            </FormLayout>

            <FormLayout>
              <hr style={{
                height: 2,
                background: "#ff5c00",
                border: "none"
              }} />
              <Group style={{
                paddingLeft: 16,
                color: "#ff5c00",
                fontSize: 32,
              }}>Куда</Group>
              <Input
                top="Адрес"
                type="text"
                name="addressTo"
                value={addressTo}
                onChange={this.onChange}
              />
              <FormLayout>
                <Select
                  top="Когда и до скольки приехать"
                  name="dayTo"
                  value={dayTo}
                  onChange={this.onChange}
                >
                  <option>Сегодня</option>
                  <option>Завтра</option>
                </Select>
                <Input
                  type="time"
                  name="timeTo"
                  value={timeTo}
                  onChange={this.onChange}
                />
              </FormLayout>
              <Input
                top="Телефон"
                type="tel"
                name="telTo"
                value={telTo}
                onChange={this.onChange}
              />
              <Input
                top="Коментарий: кв., офис и т.д."
                type="text"
                name="commentsTo"
                value={commentsTo}
                onChange={this.onChange}
              />
              <Textarea
                top="Дополнительно"
                name="additionTo"
                value={additionTo}
                onChange={this.onChange}
                style={{
                  marginBottom: 24
                }}
              />
            </FormLayout>

            <Epic tabbar={
              <Tabbar>
                <TabbarItem
                  onClick={this.onStoryChange}
                  selected={this.state.activeStory === 'price'}
                  data-story="price"
                  text={this.state.price + ' руб.'}
                ></TabbarItem>
                <TabbarItem
                  onClick={this.onStoryChange}
                  selected={this.state.activeStory === 'feed'}
                  data-story="feed"
                  text=""
                >
                  <Button>Отправить</Button>
                </TabbarItem>
              </Tabbar>
            }></Epic>
          </FormLayout>
    );
  }
}

class Main extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      activeView: "main"
    }
  }

  render() {
    return (
      <Root activeView={this.state.activeView}>
        <View id="main">
          <Panel>
            <Group style={{textAlign: "center"}}>
              <img width="305" src={logo} alt="logo"></img>
            </Group>
            {/* <PanelHeader>ЛИСКИН.КУРЬЕР</PanelHeader> */}
            <Group>
              <CellButton onClick={() => this.setState({activeView: "customer"})}>
                Оформить заказ
              </CellButton>
              <CellButton onClick={() => this.setState({activeView: "courier"})}>
                Курьер
              </CellButton>
            </Group>
          </Panel>
        </View>
        <View id="customer">
          <Panel>
            <PanelHeader 
            left={<PanelHeaderBack onClick={() => this.setState({ activeView: "main" })} />}>
              Оформление заказа
            </PanelHeader>
            <Form />
          </Panel>
        </View>
        <View id="courier">
          <Panel>
            <PanelHeader>Курьер</PanelHeader>
            <Group>
              <CellButton onClick={() => this.setState({activeView: "main"})}>
                На главную
              </CellButton>
            </Group>
          </Panel>
        </View>
      </Root>
    )
  }
}

ReactDOM.render(<Form />, document.getElementById('root'));
