import React from 'react';
import ReactDOM from 'react-dom';
import { 
  View,
  Panel,
  PanelHeader,
  PanelHeaderBack,
  Root,
  CellButton,
  Group,
  FormLayout,
  FormLayoutGroup,
  Input,
  Select,
  Textarea,
  Checkbox,
  Radio,
  Link,
  Button,
  Epic,
  Tabbar,
  TabbarItem
} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';
import logo from "./images/logoFull.png";

class Form extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      email: '',
      purpose: '',
      price: '96'
    }

    this.addressItems = [
      { label: 'Почтовый индекс', name: 'zip' },
      { label: 'Страна', name: 'country' },
      { label: 'Город', name: 'city' }
    ];

    this.onChange = this.onChange.bind(this);
  }

  onChange(e) {
    const { name, value } = e.currentTarget;
    this.setState({ [name]: value });
  }

  render() {
    const { email, purpose } = this.state;

    return (
          <FormLayout>
            <FormLayoutGroup top="Тип доставки">
              <Radio name="radio" value="w" checked>Пешком</Radio>
              <Radio name="radio" value="a">Авто</Radio>
            </FormLayoutGroup>

            <Select top="Вес посылки">
              <option name="weight" value="1" checked>До 1 кг</option>
              <option name="weight" value="5">До 5 кг</option>
              <option name="weight" value="10">До 10 кг</option>
              <option name="weight" value="15">До 15 кг</option>
            </Select>

            <hr/>

            <Input
              type="email"
              top="E-mail"
              name="email"
              value={email}
              onChange={this.onChange}
            />

            <FormLayoutGroup top="Пароль" bottom="Пароль может содержать только латинские буквы и цифры.">
              <Input type="password" placeholder="Введите пароль" />
              <Input type="password" placeholder="Повторите пароль" />
            </FormLayoutGroup>

            <Input top="Имя" />
            <Input top="Фамилия" />

            {this.addressItems.map(({ label, name }) => (
              <Input type="text" name={name} key={name} top={label} />
            ))}

            <Select
              top="Цель поездки"
              placeholder="Выберите цель поездки"
              status={purpose ? 'valid' : 'error'}
              bottom={purpose ? '' : 'Пожалуйста, укажите цель поездки'}
              onChange={this.onChange}
              value={purpose}
              name="purpose"
            >
              <option value="0">Бизнес или работа</option>
              <option value="1">Индивидуальный туризм</option>
              <option value="2">Посещение близких родственников</option>
            </Select>

            <Textarea top="О себе" />

            <Checkbox>Согласен со всем <Link>этим</Link></Checkbox>
            <Button size="xl">Зарегистрироваться</Button>
          </FormLayout>
    );
  }
}

class Main extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      activeView: "main"
    }
  }

  render() {
    return (
      <Root activeView={this.state.activeView}>
        <View id="main">
          <Panel>
            <Group style={{textAlign: "center"}}>
              <img width="305" src={logo} alt="logo"></img>
            </Group>
            {/* <PanelHeader>ЛИСКИН.КУРЬЕР</PanelHeader> */}
            <Group>
              <CellButton onClick={() => this.setState({activeView: "customer"})}>
                Оформить заказ
              </CellButton>
              <CellButton onClick={() => this.setState({activeView: "courier"})}>
                Курьер
              </CellButton>
            </Group>
          </Panel>
        </View>
        <View id="customer">
          <Panel>
            <PanelHeader 
            left={<PanelHeaderBack onClick={() => this.setState({ activeView: "main" })} />}>
              Оформление заказа
            </PanelHeader>
            <Form />
            <Epic activeStory={this.onStoryChange} tabbar={
              <Tabbar>
                <TabbarItem
                  onClick={this.onStoryChange}
                  selected={this.state.activeStory === 'price'}
                  data-story="price"
                  text={this.state.price + ' руб.'}
                ></TabbarItem>
                <TabbarItem
                  onClick={this.onStoryChange}
                  selected={this.state.activeStory === 'feed'}
                  data-story="feed"
                  text=""
                >Отправить</TabbarItem>
              </Tabbar>
            }></Epic>
          </Panel>
        </View>
        <View id="courier">
          <Panel>
            <PanelHeader>Курьер</PanelHeader>
            <Group>
              <CellButton onClick={() => this.setState({activeView: "main"})}>
                На главную
              </CellButton>
            </Group>
          </Panel>
        </View>
      </Root>
    )
  }
}

function App() {
  return(
    <Main />
  );
}

ReactDOM.render(<Form />, document.getElementById('root'));
