import React from 'react';
import ReactDOM from 'react-dom';
import { 
  View,
  Panel,
  PanelHeader,
  Root,
  CellButton,
  Group,
} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';
import logo from "./images/logoFull.png"

class Main extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      activeView: "main"
    }
  }

  render() {
    return (
      <Root activeView={this.state.activeView}>
        <View id="main">
          <Panel>
            <Group style={{textAlign: "center"}}>
              <img width="305" src={logo} alt="logo"></img>
            </Group>
            {/* <PanelHeader>ЛИСКИН.КУРЬЕР</PanelHeader> */}
            <Group>
              <CellButton onClick={() => this.setState({activeView: "customer"})}>
                Заказчик
              </CellButton>
              <CellButton onClick={() => this.setState({activeView: "courier"})}>
                Курьер
              </CellButton>
            </Group>
          </Panel>
        </View>
        <View id="customer">
          <Panel>
            <PanelHeader>Заказчик</PanelHeader>
            <Group>
              <CellButton onClick={() => this.setState({activeView: "main"})}>
                На главную
              </CellButton>
            </Group>
          </Panel>
        </View>
        <View id="courier">
          <Panel>
            <PanelHeader>Курьер</PanelHeader>
            <Group>
              <CellButton onClick={() => this.setState({activeView: "main"})}>
                На главную
              </CellButton>
            </Group>
          </Panel>
        </View>
      </Root>
    )
  }
}

function App() {
  return(
    <Main />
  );
}

ReactDOM.render(<App />, document.getElementById('root'));
