import React from 'react';
import ReactDOM from 'react-dom';
import {
    View,
    Panel,
    PanelHeader,
    PanelHeaderBack,
    Card,
    CardGrid,
    Text,
    Div,
    ModalRoot,
    ModalPage,
    ModalPageHeader,
    PanelHeaderButton,
    IS_PLATFORM_ANDROID,
    IS_PLATFORM_IOS
} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

class Order extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            activeModal: null
        }

        this.order = {
            weight: "1",
            addressFrom: 'ул. Д. Донского, д.1',
            addressTo: 'ул. Марата, д.4',
            timeFrom: '15:30',
            timeTo: '17:00',
            dayFrom: '',
            dayTo: '',
            telTo: '',
            telFrom: '',
            commentsFrom: '',
            commentsTo: '',
            additionFrom: '',
            additionTo: '',
            carry: 'Документы',
            price: '100'
        }

        this.modalBack = () => {
            this.setActiveModal(null);
        }
    }

    setActiveModal(activeModal) {
        this.setState({activeModal});
    }

    render() {
        const modal = (
            <ModalRoot
                activeModal={this.state.activeModal}
                onClose={this.modalBack}
            >
                <ModalPage id="order">
                    <ModalPageHeader
                        right={<PanelHeaderButton onClick={this.modalBack}>{IS_PLATFORM_IOS ? 'Готово' : <Icon24Done />}</PanelHeaderButton>}
                    >Test</ModalPageHeader>
                </ModalPage>
            </ModalRoot>
        );
        return(
            <View activePanel="orders" modal={modal}>
                <Panel id="orders">
                    <CardGrid style={{marginTop: 5}}>
                        <Text>Заказ №_____</Text>
                        <Card size="l" mode="shadow" onClick={()=> this.setState({activeModal: "order"})}>
                            <div style={{
                                minHeight: 96,
                                display: "flex",
                                padding: 10
                            }}>
                                <div style={{
                                    borderRight: "1px solid #ccc",
                                    paddingRight: 10,
                                    color: "#ff5c00",
                                    fontSize: 14
                                }}>
                                    {this.order.price} Руб.
                                </div>
                                <div style={{
                                    padding: "0 0 0 10px"
                                }}>
                                    <Text style={{color: "#c6c6c6"}}>
                                        Приехать до <span style={{fontWeight: "bold", color: "#ff5c00"}}>
                                            {this.order.timeFrom}
                                        </span>
                                    </Text>
                                    <Text style={{
                                        color: "#c6c6c6"
                                    }}>От: <span style={{
                                        color: "#ff5c00"
                                    }}>{this.order.addressFrom}</span></Text>
                                    <Text style={{
                                        color: "#c6c6c6"
                                    }}>До: <span style={{
                                        color: "#ff5c00"
                                    }}>{this.order.addressTo}</span></Text>
                                    <Text style={{
                                        color: "#ff5c00",
                                        marginTop: 15,
                                        // borderTop: "1px solid #c6c6c6"
                                    }}>
                                        {this.order.carry} до {this.order.weight} кг.
                                    </Text>
                                </div>
                            </div>
                        </Card>
                    </CardGrid>
                </Panel>
            </View>
        );
    }
}

function Courier() {
    return(
        <div>
            <Div style={{
                fontWeight: "bold",
                fontSize: 20,
                borderBottom: "1px solid #ccc"
            }}>Сегодня</Div>
            <Order />
        </div>
    );
}

export default Courier;
