import React from 'react';
import ReactDOM from 'react-dom';
import orders from '../../orders/orders.json';
import {
    View,
    Panel,
    PanelHeader,
    PanelHeaderBack,
    Card,
    CardGrid,
    Text,
    Div,
    ModalRoot,
    ModalPage,
    ModalPageHeader,
    PanelHeaderButton,
    IS_PLATFORM_ANDROID,
    IS_PLATFORM_IOS,
    FormLayout,
    Button
} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

function CardItem(props) {
    return(
        <CardGrid style={{marginTop: 5}}>
            <Text>Заказ №{props.count.id}</Text>
            <Card size="l" mode="shadow">
                <div style={{
                    minHeight: 96,
                    display: "flex",
                    padding: 10
                }}>
                    <div style={{
                        borderRight: "1px solid #ccc",
                        paddingRight: 10,
                        color: "#ff5c00",
                        fontSize: 14
                    }}>
                        100 Руб.
                    </div>
                    <div style={{
                        padding: "0 0 0 10px"
                    }}>
                        <Text style={{color: "#c6c6c6"}}>
                            Приехать до <span style={{fontWeight: "bold", color: "#ff5c00"}}>
                                15:30
                            </span>
                        </Text>
                        <Text style={{
                            color: "#c6c6c6"
                        }}>От: <span style={{
                            color: "#ff5c00"
                        }}>ddd</span></Text>
                        <Text style={{
                            color: "#c6c6c6"
                        }}>До: <span style={{
                            color: "#ff5c00"
                        }}>eee</span></Text>
                        <Text style={{
                            color: "#ff5c00",
                            marginTop: 15,
                            // borderTop: "1px solid #c6c6c6"
                        }}>
                            ttt до qqq кг.
                        </Text>
                    </div>
                </div>
            </Card>
        </CardGrid>
    );
}

class Order extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            activeModal: null
        }

        this.order = {
            orderNum: "846375",
            weight: "1",
            addressFrom: 'ул. Д. Донского, д.1',
            addressTo: 'ул. Марата, д.4',
            timeFrom: '15:30',
            timeTo: '17:00',
            dayFrom: '',
            dayTo: '',
            telTo: '',
            telFrom: '',
            commentsFrom: '',
            commentsTo: '',
            additionFrom: '',
            additionTo: '',
            carry: 'Документы',
            price: '100'
        }

        this.modalBack = () => {
            this.setActiveModal(null);
        }
    }

    setActiveModal(activeModal) {
        this.setState({activeModal});
    }

    render() {

        let orderArr = [];
        for (let i = 0; i < orders.length; i++) {
            orderArr.push(
                <CardItem count={orders[i]} onClick={()=> this.setState({activeModal: "order"})}/>
            );
        }

        const modal = (
            <ModalRoot
                activeModal={this.state.activeModal}
                onClose={this.modalBack}
            >
                <ModalPage
                    id="order"
                    onClose={this.modalBack}
                    header={
                        <ModalPageHeader
                            right={<PanelHeaderButton onClick={this.modalBack}>{'Готово'}</PanelHeaderButton>}
                        >Заказ №{this.order.orderNum}</ModalPageHeader>
                    }
                >

                </ModalPage>
            </ModalRoot>
        );
        return(
            <View modal={modal}>
                {orderArr}
            </View>
        );
    }
}

