import React from 'react';
import ReactDOM from 'react-dom';
import { 
  View,
  Panel,
  PanelHeader,
  PanelHeaderBack,
  Root,
  CellButton,
  Group,
  FormLayout,
  FormLayoutGroup,
  Input,
  Select,
  Textarea,
  Checkbox,
  Radio,
  Link,
  Button,
  Epic,
  Tabbar,
  TabbarItem,
  Cell,
  SimpleCell
} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';
import logo from "./images/logoFull.png";

class Form extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      addressFrom: '',
      addressTo: '',
      timeFrom: '',
      timeTo: '',
      tel: '',
      comments: '',
      addition: '',
      price: '0'
    }

    this.addressItems = [
      { label: 'Почтовый индекс', name: 'zip' },
      { label: 'Страна', name: 'country' },
      { label: 'Город', name: 'city' }
    ];

    this.onChange = this.onChange.bind(this);
  }

  onChange(e) {
    const { name, value } = e.currentTarget;
    this.setState({ [name]: value });
  }

  render() {
    const { addressFrom, addressTo, timeFrom, timeTo, tel, comments, addition } = this.state;

    return (
          <FormLayout>
            <FormLayoutGroup top="Тип доставки">
              <Radio name="radio" value="w">Пешком</Radio>
              <Radio name="radio" value="a">Авто</Radio>
            </FormLayoutGroup>

            <Select top="Вес посылки">
              <option name="weight" value="1">До 1 кг</option>
              <option name="weight" value="5">До 5 кг</option>
              <option name="weight" value="10">До 10 кг</option>
              <option name="weight" value="15">До 15 кг</option>
            </Select>
            <FormLayoutGroup>
              <hr style={{
                height: 2,
                background: "#ff5c00",
                border: "none"
              }} />
              <Group style={{
                paddingLeft: 16,
                color: "#ff5c00",
                fontSize: 32,
                }}>Откуда</Group>
              <Input
                type="text"
                name="addressFrom"
                value={addressFrom}
                onChange={this.onChange}
                placeholder="Адрес"
              />
              <Input
                type="text"
                name="TimeTo"
                placeholder="Когда приехать на этот адрес"
              />
              <Input
                type="tel"
                name="tel"
                placeholder="Телефон"
              />
              <Input
                type="text"
                name="comments"
                placeholder="Коментарий: кв., офис и т.д."
              />
              <Textarea
                placeholder="Дополнительно"
              />
            </FormLayoutGroup>

            <Checkbox>Согласен со всем <Link>этим</Link></Checkbox>
            <Button size="xl">Зарегистрироваться</Button>
          </FormLayout>
    );
  }
}

class Main extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      activeView: "main"
    }
  }

  render() {
    return (
      <Root activeView={this.state.activeView}>
        <View id="main">
          <Panel>
            <Group style={{textAlign: "center"}}>
              <img width="305" src={logo} alt="logo"></img>
            </Group>
            {/* <PanelHeader>ЛИСКИН.КУРЬЕР</PanelHeader> */}
            <Group>
              <CellButton onClick={() => this.setState({activeView: "customer"})}>
                Оформить заказ
              </CellButton>
              <CellButton onClick={() => this.setState({activeView: "courier"})}>
                Курьер
              </CellButton>
            </Group>
          </Panel>
        </View>
        <View id="customer">
          <Panel>
            <PanelHeader 
            left={<PanelHeaderBack onClick={() => this.setState({ activeView: "main" })} />}>
              Оформление заказа
            </PanelHeader>
            <Form />
            <Epic activeStory={this.onStoryChange} tabbar={
              <Tabbar>
                <TabbarItem
                  onClick={this.onStoryChange}
                  selected={this.state.activeStory === 'price'}
                  data-story="price"
                  text={this.state.price + ' руб.'}
                ></TabbarItem>
                <TabbarItem
                  onClick={this.onStoryChange}
                  selected={this.state.activeStory === 'feed'}
                  data-story="feed"
                  text=""
                >Отправить</TabbarItem>
              </Tabbar>
            }></Epic>
          </Panel>
        </View>
        <View id="courier">
          <Panel>
            <PanelHeader>Курьер</PanelHeader>
            <Group>
              <CellButton onClick={() => this.setState({activeView: "main"})}>
                На главную
              </CellButton>
            </Group>
          </Panel>
        </View>
      </Root>
    )
  }
}

function App() {
  return(
    <Main />
  );
}

ReactDOM.render(<Form />, document.getElementById('root'));
